import java.util.ArrayList;

public class SetTheory {
    public static ArrayList<Integer> generateSet(int min, int max) {
        ArrayList<Integer> integerList = new ArrayList<>();

        if (min < 0) {
            min = 0;
        }
        if (max > 100) {
            max = 100;
        }
        for (int i = min; i < max; i++) {
            integerList.add(i);
        }
        return integerList;
    }

    public static ArrayList<Integer> union(ArrayList<Integer> a, ArrayList<Integer> b) {
        ArrayList<Integer> unionList = new ArrayList<>();
            for (int i : a) {
                unionList.add(i);
            }
            for (int i : b) {
                if (!unionList.contains(i)) {
                    unionList.add(i);
                }
            }
        return unionList;
    }

    public static ArrayList<Integer> intersection(ArrayList<Integer> a, ArrayList<Integer> b) {
        ArrayList<Integer> intersectionList = new ArrayList<>();
        for (int i : a) {
            if (b.contains(i)) {
                intersectionList.add(i);
            }
        }
        return intersectionList;
    }
    public static ArrayList<Integer> complement(ArrayList<Integer> a) {
        ArrayList<Integer> complementList = new ArrayList<>();
        for (int i = 0; i < 100; i++) {
            if (!a.contains(i)) {
                complementList.add(i);
            }
        }
        return complementList;
    }
    public static int cardinality(ArrayList<Integer> a) {
        return a.size();
    }
    public static int cardinalityOfUnion(ArrayList<Integer> a, ArrayList<Integer> b) {
        ArrayList<Integer> list = union(a,b);
        return cardinality(list);
    }
    public static void main(String[] args) {
        SetTheory test = new SetTheory();
        ArrayList<Integer> lsitA = generateSet(0, 0);
        ArrayList<Integer> lsitB = generateSet(20, 30);
        System.out.println(test.union(lsitA, lsitB));
        System.out.println(test.cardinalityOfUnion(lsitA,lsitB));
    }
}

public class Arrays {
    public static int average(int[] array) {
        int sum = 0;
        for (int i : array) {
            sum += i;
        }
        return sum / array.length;
    }
    public static double average(double[] array) {
        double sum = 0.0;
        for (double d : array) {
            sum += d;
        }
        return sum / array.length;
    }

    public static int smallestElement(int[] array) {
        int tmp = Integer.MAX_VALUE;
        for (int i : array) {
            tmp = (i<tmp) ? i : tmp; 
        }
        return tmp;
    }

    public static int[] reverse(int[] array) {
        int[] reversed = new int[array.length];
        int index = array.length-1;
        for (int i : array) {
            reversed[index] = i;
            index--;
        }
        return reversed;
    }

    public static int[] evenNumbers(int[] array) {
        int evenCount = 0, index = 0;
        for (int i : array) {
            if (i % 2 == 0) {
                evenCount++;
            }
        }
        int[] evenArray = new int[evenCount];
        for (int i : array) {
            if (i % 2 == 0) {
                evenArray[index] = i;
                index++;
            }
        }
        return evenArray;
    }
}
